        $string = $null
        $blob | ForEach-Object -Process {
          $string = ("{0} {1}.ToString('X2')," -f $string, $_)
        }

        $hstiStatus = New-Object -TypeName bool
        #$hr = [HstiTest3]::QueryHSTI([ref] $hstiStatus)

